// Test Screen for PostWidget - For development testing\n// This screen shows sample posts to test our PostWidget implementation\n\nimport 'package:flutter/material.dart';\nimport '../../core/theme/app_theme.dart';\nimport '../../models/social_feed/post_model.dart';\nimport '../../models/social_feed/comment_model.dart';\nimport '../../models/social_feed/geographic_targeting.dart';\nimport '../../widgets/social_feed/post_widget.dart';\n\nclass PostWidgetTestScreen extends StatelessWidget {\n  const PostWidgetTestScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text('PostWidget Test'),\n        backgroundColor: AppTheme.talowaGreen,\n        foregroundColor: Colors.white,\n      ),\n      body: ListView(\n        padding: const EdgeInsets.symmetric(vertical: 16),\n        children: [\n          // Sample post 1 - Simple text post\n          PostWidget(\n            post: _createSamplePost1(),\n            onLike: () => debugPrint('Liked post 1'),\n            onComment: () => debugPrint('Comment on post 1'),\n            onShare: () => debugPrint('Share post 1'),\n          ),\n          \n          const SizedBox(height: 16),\n          \n          // Sample post 2 - Post with images\n          PostWidget(\n            post: _createSamplePost2(),\n            onLike: () => debugPrint('Liked post 2'),\n            onComment: () => debugPrint('Comment on post 2'),\n            onShare: () => debugPrint('Share post 2'),\n          ),\n          \n          const SizedBox(height: 16),\n          \n          // Sample post 3 - Emergency post\n          PostWidget(\n            post: _createSamplePost3(),\n            onLike: () => debugPrint('Liked post 3'),\n            onComment: () => debugPrint('Comment on post 3'),\n            onShare: () => debugPrint('Share post 3'),\n          ),\n        ],\n      ),\n    );\n  }\n\n  PostModel _createSamplePost1() {\n    return PostModel(\n      id: 'post_1',\n      authorId: 'user_1',\n      authorName: 'Ravi Kumar',\n      authorRole: 'village_coordinator',\n      content: 'Great news! We successfully got patta documents for 15 families in our village. The process took 6 months but persistence paid off. #LandRights #Success #TalowaWins',\n      category: PostCategory.successStory,\n      createdAt: DateTime.now().subtract(const Duration(hours: 2)),\n      likesCount: 24,\n      commentsCount: 8,\n      sharesCount: 3,\n      viewsCount: 156,\n      hashtags: ['LandRights', 'Success', 'TalowaWins'],\n      recentComments: [\n        CommentModel(\n          id: 'comment_1',\n          postId: 'post_1',\n          authorId: 'user_2',\n          authorName: 'Priya Sharma',\n          content: 'Congratulations! This gives hope to all of us.',\n          createdAt: DateTime.now().subtract(const Duration(minutes: 30)),\n        ),\n        CommentModel(\n          id: 'comment_2',\n          postId: 'post_1',\n          authorId: 'user_3',\n          authorName: 'Suresh Reddy',\n          content: 'Amazing work! Can you share the process?',\n          createdAt: DateTime.now().subtract(const Duration(minutes: 15)),\n        ),\n      ],\n    );\n  }\n\n  PostModel _createSamplePost2() {\n    return PostModel(\n      id: 'post_2',\n      authorId: 'user_2',\n      authorName: 'Dr. Lakshmi Devi',\n      authorRole: 'district_coordinator',\n      content: 'Important legal update: New guidelines for land registration have been released. All coordinators should review these changes. Meeting scheduled for next week.',\n      category: PostCategory.legalUpdate,\n      createdAt: DateTime.now().subtract(const Duration(hours: 5)),\n      likesCount: 45,\n      commentsCount: 12,\n      sharesCount: 18,\n      viewsCount: 234,\n      imageUrls: [\n        'https://via.placeholder.com/400x300/4CAF50/FFFFFF?text=Legal+Document+1',\n        'https://via.placeholder.com/400x300/2196F3/FFFFFF?text=Legal+Document+2',\n      ],\n      documentUrls: [\n        'https://example.com/legal_guidelines.pdf',\n      ],\n      hashtags: ['LegalUpdate', 'Guidelines'],\n      isPinned: true,\n      targeting: GeographicTargeting(\n        state: 'Telangana',\n        district: 'Warangal',\n      ),\n    );\n  }\n\n  PostModel _createSamplePost3() {\n    return PostModel(\n      id: 'post_3',\n      authorId: 'user_3',\n      authorName: 'Rajesh Naidu',\n      authorRole: 'member',\n      content: 'ðŸš¨ URGENT: Land grabbing attempt in progress at Survey No. 123, Kondapur village. Need immediate support from coordinators and legal team. Please contact me ASAP.',\n      category: PostCategory.emergency,\n      createdAt: DateTime.now().subtract(const Duration(minutes: 30)),\n      likesCount: 8,\n      commentsCount: 15,\n      sharesCount: 25,\n      viewsCount: 89,\n      hashtags: ['Emergency', 'LandGrabbing', 'Help'],\n      isEmergency: true,\n      targeting: GeographicTargeting(\n        state: 'Telangana',\n        district: 'Rangareddy',\n        mandal: 'Serilingampally',\n        village: 'Kondapur',\n      ),\n      recentComments: [\n        CommentModel(\n          id: 'comment_3',\n          postId: 'post_3',\n          authorId: 'user_4',\n          authorName: 'Advocate Srinivas',\n          content: 'I am on my way. Have you filed a police complaint?',\n          createdAt: DateTime.now().subtract(const Duration(minutes: 10)),\n        ),\n      ],\n    );\n  }\n}\n"